DROP TABLE IF EXISTS `shiou_api`;
CREATE TABLE `shiou_api` (
`id`  int(4) NOT NULL COMMENT '索引id' ,
`api_type`  int(4) NOT NULL COMMENT '接口类型ID' ,
`api_request`  text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '请求参数映射' ,
`request_type`  varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '请求类型  http 或 webservice 或 https' ,
`request_param_type`  varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '请求方式 post 或get' ,
`request_data`  text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '如果是webservice方式，并且是用http方式请求，xml字符串保存在这里' ,
`response_data_type`  varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '返回数据类型 json 或xml' ,
`api_response`  varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '返回参数映射' ,
`api_url`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'api地址' ,
`header`  varchar(150) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT '' COMMENT 'header头信息' ,
`is_sign`  tinyint(1) UNSIGNED NULL DEFAULT 0 COMMENT '是否需要签名，0否，1是' ,
`from_id`  int(100) NOT NULL DEFAULT 0 COMMENT '哪一个来源的id（total_from表的id）' ,
PRIMARY KEY (`id`)
)
ENGINE=InnoDB
DEFAULT CHARACTER SET=utf8 COLLATE=utf8_general_ci
COMMENT='西单接口表'

;

-- ----------------------------
-- Table structure for `shiou_auto_form`
-- ----------------------------
DROP TABLE IF EXISTS `shiou_auto_form`;
CREATE TABLE `shiou_auto_form` (
`id`  int(10) NOT NULL ,
`isenable`  smallint(1) NOT NULL DEFAULT 1 COMMENT '1开启，0不开启，默认开启' ,
`ischange`  smallint(1) NOT NULL DEFAULT 0 COMMENT '1开启，0不开启，默认不开启' ,
`form_key_id`  int(10) NOT NULL COMMENT '表单id' ,
`isrequired`  smallint(1) NOT NULL DEFAULT 1 COMMENT '是否必填，1是，0否' ,
`placeholder`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT 'input内的提示内容' ,
`sub`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '字段后面的提示' ,
`value`  varchar(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '默认值，对所有字段类型全部符合' ,
`minlength`  varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '1' COMMENT '输入框最短多少位,转int' ,
`maxlength`  varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '200' COMMENT '输入框最多多少位，转int' ,
`sort`  smallint(20) NOT NULL DEFAULT 1 COMMENT '排序' ,
`type`  smallint(1) NOT NULL DEFAULT 1 COMMENT '注册或是修改表单，1注册，0修改' ,
