DROP TABLE IF EXISTS `taiguli_auto_form`;
CREATE TABLE `taiguli_auto_form` (
`id`  int(10) NOT NULL ,
`isenable`  smallint(1) NOT NULL DEFAULT 1 COMMENT '1开启，0不开启，默认开启' ,
`ischange`  smallint(6) NOT NULL DEFAULT 0 COMMENT '1开启，0不开启，默认不开启' ,
